sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('x_1093221_netwisdo.IncidentAverageCalculator');
    ga.addParam('sysparm_name', 'calculateAverage');
    var student_id = g_form.getValue('student_id');
    var u_course_id = g_form.getValue('study_schedule_id');
    ga.addParam('sysparm_studentID', student_id);
    ga.addParam('sysparm_courseID', u_course_id);
    // 假设您有一个名为'caller_id'的引用字段，并且您想获取这个引用的用户记录
    debugger;
    ga.getXML(function (response) {
        //   // 在这里处理返回的数据
        var answer = JSON.parse(
            response.responseXML.documentElement.getAttribute('answer')
        );
        if (answer) {
            g_form.setValue('final_score', answer);
        }
    });

    g_form.addInfoMessage('求学生成绩的平均值');
}
