sys_script_include.script:
var GlideAjax_demo = Class.create();
GlideAjax_demo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
getInfo : function(){
	var sys_id = this.getParameter('sys_id').toString();
	//var student_id = this.getParameter('student_id');

	var gr = new GlideRecord('x_1093221_netwisdo_x_student_study_schedule_condition');
	gr.addQuery('sys_id', sys_id);
	//gr.addQuery('student_id', student_id);
	//gr.addEncodedQuery('student_id', student_id);
	gr.addQuery('study_condition', '1');
	gr.query();

	if(gr.next()){
		//获取每条数据中参照字段的显示值
		gs.addInfoMessage('student_id->'+gr.getDisplayValue("student_id"));
		//查看查询结果的数量
		gs.addInfoMessage('GlideAjax_demo★★'+gr.getRowCount());
		return true;	
	}
	return false;
},
    type: 'GlideAjax_demo'
});
