sys_script_include.script:
var IncidentAverageCalculator = Class.create();
IncidentAverageCalculator.prototype = Object.extendsObject(
    global.AbstractAjaxProcessor,
    {
        calculateAverage: function () {
            var result = 0;
            var studentID = this.getParameter('sysparm_studentID').toString();
            var courseID = this.getParameter('sysparm_courseID').toString();

            var gr = new GlideRecord(
                'x_1093221_netwisdo_x_student_study_schedule_condition'
            );
            gs.info('sysparm_studentID---' + studentID);
            gs.info('sysparm_courseID---' + courseID);

            gr.addQuery('student_id', studentID);
            gr.addQuery('study_schedule_id', courseID);

            gr.query();
            gs.info('gr.getRowCount()---' + gr.getRowCount());
            if (gr.next()) {
                var test_1 = Number(gr.getValue('test_1').toString());
                var test_2 = Number(gr.getValue('test_2').toString());
				if(test_1 < 60){
					result = (test_1 + test_2) / 2;
				}else{
					result = test_1 ;
				}

            }
            //return JSON.stringify(result);
            return JSON.stringify(result);
        },
        type: 'IncidentAverageCalculator',
    }
);
